import React, { useState } from "react";
import { useForm, Controller } from "react-hook-form";
import DatePicker from "react-datepicker";
import "react-datepicker/dist/react-datepicker.css";
import states from './States';
import Dropdown from 'react-dropdown';
import 'react-dropdown/style.css';


const FormsEmployee = () => {
  const {
    register,
    handleSubmit,
    formState,
    errors,
    setError,
    control,
  } = useForm();



  const onSubmit = (data) => {
    console.log(data);
  };

  return (
    <>
      <div class="container">
        <h2>Create Employee</h2>
        <form onSubmit={handleSubmit(onSubmit)} action="#" id="create-employee">
          <label for="first-name">First Name</label>
          <input {...register("first-name")} type="text" id="first-name" />

          <label for="last-name">Last Name</label>
          <input {...register("last-name")} type="text" id="last-name" />

          <div className="container-picker">
            <label for="birthDate">Date of Birth</label>

            <Controller
              name="birthDate"
              defaultValue={null}
              control={control}
              render={({ field: { onChange, value } }) => (
                <DatePicker
                  placeholderText="Select BirthDay"
                  selected={value}
                  onChange={onChange}

                />
              )}
            />
          </div>
          <div className="container-picker">
            <label for="startDate">Start Date</label>

            <Controller
              name="startDate"
              defaultValue={null}
              control={control}
              render={({ field: { onChange, value } }) => (
                <DatePicker
                  placeholderText="Select Start Date"
                  selected={value}
                  onChange={onChange}
                />
              )}
            />
          </div>

          <fieldset class="address">
            <legend>Address</legend>

            <label for="street">Street</label>
            <input {...register('street')} id="street" type="text" />

            <label for="city">City</label>
            <input {...register('city')} id="city" type="text" />

            <label for="city"></label>
<Dropdown options={states.name} onChange={onchange} value={states.name} placeholder="Select an option" />;

            <label for="zip-code">Zip Code</label>
            <input {...register('zip-code')} id="zip-code" type="number" />
          </fieldset>

          <label for="department">Department</label>
          <select name="department" id="department">
            <option>Sales</option>
            <option>Marketing</option>
            <option>Engineering</option>
            <option>Human Resources</option>
            <option>Legal</option>
          </select>
          <button onclick="saveEmployee()">Save</button>
        </form>
      </div>
    </>
  );
};

export default FormsEmployee;
