import React, {useState} from "react";
import {useForm, Controller} from 'react-hook-form'
import DatePicker from 'react-datepicker'
import 'react-datepicker/dist/react-datepicker.css'



const FormsEmployee = () => {

const {register, handleSubmit, formState, errors, setError,control}= useForm();

const [startDate, setStartDate]= useState(new Date());

const onSubmit = data =>{
    console.log(data)
}



  return (
    <>
      <div class="container">
        <h2>Create Employee</h2>
        <form onSubmit={handleSubmit(onSubmit)} action="#" id="create-employee">
          <label for="first-name">First Name</label>
          <input type="text" id="first-name" />

          <label for="last-name">Last Name</label>
          <input type="text" id="last-name" />

           <div className='date-picker'>
          <label for="birth">Date of Birth</label>

          <Controller
          name='birthDate'
          defaultValue={null}
          control={control} render={({onChange, onBlur, value ,ref})=>(
               <DatePicker
               defaultValue={true}
               selected={value}
               onBlur={onBlur}
               onChange={onChange}
               /> 
          )}
           /> 


          </div>

          <label for="start-date">Start Date</label>
          <input id="start-date" type="text" />

          <fieldset class="address">
            <legend>Address</legend>

            <label for="street">Street</label>
            <input id="street" type="text" />

            <label for="city">City</label>
            <input id="city" type="text" />

            <label for="state">State</label>
            <select name="state" id="state"></select>

            <label for="zip-code">Zip Code</label>
            <input id="zip-code" type="number" />
          </fieldset>

          <label for="department">Department</label>
          <select name="department" id="department">
            <option>Sales</option>
            <option>Marketing</option>
            <option>Engineering</option>
            <option>Human Resources</option>
            <option>Legal</option>
          </select>
          <button onclick="saveEmployee()">Save</button>
        </form>
      </div>

    </>
  );
};

export default FormsEmployee;
