import React, { useEffect, useState } from "react";
import firebase from "../utils/firebaseConfig";
import { useTable, useSortBy, useFilters, useGlobalFilter,useAsyncDebounce } from "react-table";
import columns from "../data/table";

import GlobalFilterTable from "./GlobalFilterTable";



const TableEmployee = () => {
  const [data, setData] = useState([]);
  // get data with firebase
  useEffect(() => {
    const tableDB = firebase.database().ref("create-employee");

    tableDB.on("value", (snapshot) => {
      let previousTable = snapshot.val();
      let list = [];
      for (let id in previousTable) {
        list.push({ id, ...previousTable[id] });
      }

      setData(list);
      console.log(data);
    });
  }, []);


  const {
    getTableProps,
    getTableBodyProps,
    headerGroups,
    rows,
    prepareRow,
    state,
    visibleColumns,
    preGlobalFilteredRows,
    setGlobalFilter,
  } = useTable(
    {
      columns,
      data,
     },
    useFilters, // useFilters!
    useGlobalFilter, // useGlobalFilter!
    useSortBy
  )


  return (
      <>
    <GlobalFilterTable

    globalFilter={globalFilterTable}
    setGlobalFilter={setGlobalFilter}
  />
    <table {...getTableProps()}>
      <thead>
        {headerGroups.map((headerGroup) => (
          <tr {...headerGroup.getHeaderGroupProps()}>
            {headerGroup.headers.map((column) => (
              <th {...column.getHeaderProps(column.getSortByToggleProps())}>
                {column.render("Header")}
                <span>
                  {column.isSorted
                    ? column.isSortedDesc
                      ? "⬇️"
                      : " ⬆️"
                    :  '↕️'}
                </span>
              </th>
            ))}
          </tr>
        ))}
      </thead>
      <tbody {...getTableBodyProps()}>
        {rows.map((row, i) => {
          prepareRow(row);
          return (
            <tr {...row.getRowProps()}>
              {row.cells.map((cell) => {
                return <td {...cell.getCellProps()}>{cell.render("Cell")}</td>;
              })}
            </tr>
          );
        })}
      </tbody>
    </table>
    </>
  );
};

export default TableEmployee;
